  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-4xl font-bold mb-2">
          Welcome to <span className="text-gradient">CRM Escort AI</span>
        </h1>
        <p className="text-muted-foreground text-lg">
