import { useQuery } from '@tanstack/react-query';
import { MessageSquare, Users, Calendar as CalendarIcon, Zap } from 'lucide-react';
import { AIOrb } from '@/components/dashboard/AIOrb';
import { StatsCard } from '@/components/dashboard/StatsCard';
import { messagesApi } from '@/lib/api/messages';
import { contactsApi } from '@/lib/api/contacts';
import { eventsApi } from '@/lib/api/events';
import { Card } from '@/components/ui/card';
import { formatRelativeTime } from '@/lib/utils/date';

export default function Dashboard() {
  const { data: messages = [] } = useQuery({
    queryKey: ['messages'],
    queryFn: () => messagesApi.getMessages({ limit: 5 }),
  });

  const { data: contacts = [] } = useQuery({
    queryKey: ['contacts'],
    queryFn: () => contactsApi.getContacts({ limit: 100 }),
  });

  const { data: events = [] } = useQuery({
    queryKey: ['events'],
    queryFn: () => eventsApi.getEvents({ limit: 100 }),
  });

  const upcomingEvents = events.filter(
    (event) => new Date(event.start_time) > new Date()
  ).slice(0, 5);

  return (
    <div className="space-y-8">
      {/* Header */}
      <div className="text-center">
        <h1 className="text-4xl font-bold mb-2">
          Welcome to <span className="text-gradient">CRM Escort AI</span>
        </h1>
        <p className="text-muted-foreground text-lg">
          Your intelligent assistant for managing conversations and relationships
        </p>
      </div>

      {/* AI Orb */}
      <div className="flex justify-center py-8">
        <AIOrb state="idle" size="lg" />
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatsCard
          title="Total Messages"
          value={messages.length}
          icon={MessageSquare}
          color="blue"
          trend={{ value: 12, isPositive: true }}
        />
        <StatsCard
          title="Contacts"
          value={contacts.length}
          icon={Users}
          color="violet"
          trend={{ value: 8, isPositive: true }}
        />
        <StatsCard
          title="Upcoming Events"
          value={upcomingEvents.length}
          icon={CalendarIcon}
          color="cyan"
        />
        <StatsCard
          title="AI Processed"
          value={messages.filter((m) => m.ai_processed).length}
          icon={Zap}
          color="pink"
          trend={{ value: 15, isPositive: true }}
        />
      </div>

      {/* Recent Activity */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Recent Messages */}
        <Card className="glass-panel p-6">
          <h2 className="text-xl font-bold mb-4 flex items-center gap-2">
            <MessageSquare className="h-5 w-5 text-primary" />
            Recent Messages
