          <div className="space-y-3">
            {messages.length === 0 ? (
              <div className="text-center py-8">
                <img
                  src="/assets/empty-state-messages.svg"
                  alt="No messages"
                  className="w-32 h-32 mx-auto mb-4 opacity-50"
                />
                <p className="text-muted-foreground">No messages yet</p>
              </div>
